import {useEffect, useState } from "react";


function ShowCard({formData}){
    const [showData, setShowData] = useState([formData]);

    function addShow(newShow){
    setShowData([...showData, newShow]) // Updating movies state.
    }

   useEffect(() => {
         fetch("http://localhost:3000/showData")
        .then(r => r.json())
        .then(data => addShow(data))
        }, [])




        console.log(showData);
    return (
        <div> 
        <h2>This is the Show Card</h2> 
        <h1>List your favorite shows</h1>
        <ul>
            {showData.map(show => (
                <li key={show.id}>
                    <img src={show.image} alt={show.name} />
                    <h3> Show: {show.name}</h3>
                </li>
            ))}
        </ul>
        </div>
       );
}

export default ShowCard;
